import React, { useState } from 'react';
import Intro from './intro_board'
import Spinner from 'react-bootstrap/Spinner'
import Game from './game'
import Score from './scoreboard'
import './css/quiz.css'

function App() {
	var player = null
	var q_data = null;
	var corr_ans = null;
	var count_q = 0;
	var stat = 0;
	var [g_ini, g_sw] = useState(<Intro play_f={() => { show_quizes() }} />)


	var show_quizes = async () => {
		var dif_sel = document.getElementById('diff_val').value;
		var top_sel = document.getElementById('topic_val').value;
		player = document.getElementById('name').value;
		g_sw(<Spinner animation="border" variant="warning" />)
		await fetch('https://opentdb.com/api.php?amount=10&category=' + top_sel + '&difficulty=' + dif_sel + '&type=multiple')
			.then(res => res.json())
			.then(data => { q_data = data.results; });
		game(q_data, count_q);
	}

	var res = (answer) => {
		if (corr_ans === answer) {
			stat += 1;
		}

		if (count_q === 9) {
			result();
		}
		else {
			console.log(count_q);
			count_q += 1;
			game(q_data, count_q);
		}
	}


	var game = (questions, counter) => {
		var data_q = [];
		var corr_place = Math.floor(Math.random() * 4);
		var count = 0;
		for (var j = 0; j < 4; j++) {
			if (j !== corr_place) {
				data_q.push(questions[counter].incorrect_answers[count])
				count++;
			}
			else {
				data_q.push(questions[counter].correct_answer);
			}
		}
		corr_ans = questions[counter].correct_answer;

		//the game component renders here
		g_sw(<Game
			p_name={player}
			q_quiz={questions[counter].question}
			a_1={data_q[0]}
			a_2={data_q[1]}
			a_3={data_q[2]}
			a_4={data_q[3]}
			chk_ans1={() => { chk_ans1() }}
			chk_ans2={() => { chk_ans2() }}
			chk_ans3={() => { chk_ans3() }}
			chk_ans4={() => { chk_ans4() }}
		/>)




		/*//stopwatch

		var time_rem = 30;

		setInterval(timer, 1000);
		var timer = () => {
			if (time_rem === -1) {
				clearInterval();
				if (count_q == 9) {

				}
				else {
					count_q += 1;
					game(q_data, count_q);
				}

			}
			else {
				console.log(Math.floor(time_rem));
				time_rem -= 1;
			}
		}*/



		var chk_ans1 = () => {
			var answer = document.getElementById('game_but1').name;
			res(answer);
		}

		var chk_ans2 = () => {
			var answer = document.getElementById('game_but2').name;
			res(answer);
		}

		var chk_ans3 = () => {
			var answer = document.getElementById('game_but3').name;
			res(answer);
		}

		var chk_ans4 = () => {
			var answer = document.getElementById('game_but4').name;
			res(answer);
		}


	}

	var result = () => {
		if (stat === 9 || stat === 10) {
			g_sw(<Score
				marks={stat}
				end_msg="EXCELLENT WORK!!!"
				reset={() => reset()}
			/>)
		}
		else if (stat >= 5 && stat < 9) {
			g_sw(<Score
				marks={stat}
				end_msg="GOOD JOB!!"
			/>)
		}
		else {
			g_sw(<Score
				marks={stat}
				end_msg="IT'S ALRIGHT"
			/>)
		}

	}

	var reset = () => {
		player = null
		q_data = null;
		corr_ans = null;
		count_q = 0;
		stat = 0;
		g_sw(<Intro play_f={() => { show_quizes() }} />)
	}



	return (
		<div className="App">
			{g_ini}
		</div>
	);
}

export default App;
