import React, { useState } from 'react';
import Intro from './intro_board'
import Spinner from 'react-bootstrap/Spinner'
import Game from './game'
import './css/quiz.css'

function App() {
  var player = null
  var q_data = null;
  var [g_ini,g_sw] = useState(<Intro play_f = {()=>{show_quizes()}}/>)


  var show_quizes = async()=>{
    var dif_sel = document.getElementById('diff_val').value;
    var top_sel = document.getElementById('topic_val').value;
	player = document.getElementById('name').value;
    console.log(dif_sel+" "+top_sel);
	g_sw(<Spinner animation = "border" variant = "warning"/>)
    await fetch('https://opentdb.com/api.php?amount=10&category='+top_sel+'&difficulty='+dif_sel+'&type=multiple')
    .then(res=>res.json())
    .then(data=>{q_data = data.results;console.log(data.results)});
	game(player,q_data);
  }












  var game = (player,questions)=>{
	  for(var i = 0;i<10;i++){

		//data segregation part


		  var data_q = [];
		  var corr_place = Math.floor(Math.random() * 4);
		  var count = 0;
		  for(var j = 0;j<4;j++){
			  if(j!==corr_place){
				data_q.push(questions[i].incorrect_answers[count])
				count++;
			  }
			  else{
				data_q.push(questions[i].correct_answer);
			  }
			}
			console.log(data_q);



			//the game component renders here
			g_sw(<Game
				p_name = {player}
				q_quiz = {questions[i].question}
				a_1 = {data_q[0]}
				a_2 = {data_q[1]}
				a_3 = {data_q[2]}
				a_4 = {data_q[3]}
				chk_ans = {()=>{chk_ans()}}
			/>)


			//stopwatch



			var time_rem = 30*9;
			setInterval(timer,1000);
            var timer = ()=>{
	            if(time_rem ===-1){
		            clearInterval();
	            }
	            else{
		            console.log(Math.floor(time_rem/9));
		            time_rem -= 1;
	            }
            }

			var chk_ans = ()=>{
				var answer = document.getElementsByClassName('game_but').name;
				clearInterval();
				console.log(answer);
			}





		}
	}



  return (
    <div className="App">
      {g_ini}
    </div>
  );
}

export default App;
